// ProductList.js
import React, { useState } from 'react';
import Modal from './Modal'; // 모달 컴포넌트 불러오기
import './ProductList.css';

function ProductList() {
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const products = [
    { id: 1, name: 'Revuelto', price: "700,000", imageUrl: '/carimg/r1.jpg' },
    { id: 2, name: 'Urus', price: "300,000", imageUrl: '/carimg/u1.jpg' },
    { id: 3, name: 'K5 DLS', price: "30,000", imageUrl: '/carimg/k5.jpg' },
  ];

  const openModal = (product) => {
    setSelectedProduct(product);
    setModalOpen(true);
  }

  const closeModal = () => {
    setModalOpen(false);
  }

  return (
    <div>
      <h2>상품 목록</h2>
      <ul className="product-list">
        {products.map(product => (
          <li key={product.id} className="product-item" onClick={() => openModal(product)}>
            <img src={process.env.PUBLIC_URL + product.imageUrl} alt={product.name} className="product-image" />
            <div>
              <strong>{product.name}</strong>: ${product.price}
            </div>
          </li>
        ))}
      </ul>
      <Modal isOpen={modalOpen} onClose={closeModal} product={selectedProduct} />
    </div>
  );
}

export default ProductList;
