// ProductList.js
import React, { useState } from 'react';
import Modal from './Modal';

function ProductList() {
  const [selectedProduct, setSelectedProduct] = useState(null);

  const products = [
    { id: 1, name: 'Revuelto', price: "700,000", imageUrl: '/carimg/r1.jpg' },
    { id: 2, name: 'Urus', price: "300,000", imageUrl: '/carimg/u1.jpg' },
    { id: 3, name: 'K5 DLS', price: "30,000", imageUrl: '/carimg/k5.jpg' },
  ];

  const openModal = (productId) => {
    const selectedProduct = products.find(product => product.id === productId);
    setSelectedProduct(selectedProduct);
  };

  const closeModal = () => {
    setSelectedProduct(null);
  };

  return (
    <div>
      <h2>Product List</h2>
      <ul className="product-list"> 
        {products.map(product => (
          <li key={product.id} className="product-item"> 
            {/* 이미지 클릭 시 모달 열기 */}
            <img src={process.env.PUBLIC_URL + product.imageUrl} alt={product.name} className="product-image" onClick={() => openModal(product.id)} />
            <div>
              <strong>{product.name}</strong>: ${product.price}
            </div>
            {/* 선택된 제품에 대한 모달 */}
            {selectedProduct && selectedProduct.id === product.id && <Modal product={selectedProduct} closeModal={closeModal} />}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductList;
